void qmul_Outputs_wrapper(const real_T *q1,
                          const real_T *q2,
                          real_T *qres)
{
/* %%%-SFUNWIZ_wrapper_Outputs_Changes_BEGIN --- EDIT HERE TO _END */
/* This sample sets the output equal to the input
           y0[0] = u0[0];
  For complex signals use: y0[0].re = u0[0].re;
                           y0[0].im = u0[0].im;
                           y1[0].re = u1[0].re;
                           y1[0].im = u1[0].im;*/
  qres[3] = q1[3]*q2[3] - q1[0]*q2[0] - q1[1]*q2[1] - q1[2]*q2[2];
  qres[0] = q1[0]*q2[3] + q1[1]*q2[2] - q1[2]*q2[1] + q1[3]*q2[0];
  qres[1] = -q1[0]*q2[2] + q1[1]*q2[3] + q1[2]*q2[0] + q1[3]*q2[1];
  qres[2] = q1[0]*q2[1] - q1[1]*q2[0] + q1[2]*q2[3] + q1[3]*q2[2];

/* %%%-SFUNWIZ_wrapper_Outputs_Changes_END --- EDIT HERE TO _BEGIN */
}

