/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    const real_T  *q1  = (const real_T*) ssGetInputPortSignal(S,0);
    const real_T  *q2  = (const real_T*) ssGetInputPortSignal(S,1);
    real_T        *qres  = (real_T *)ssGetOutputPortRealSignal(S,0);

    qmul_Outputs_wrapper(q1,q2,qres);
}

